SELECT      f.id AS "Id do Arquivo",     f.filename AS "Nome do Arquivo",     f.filepath AS "Caminho do Arquivo",     SUM(CASE          WHEN l.eventname = '\\core\\event\\user_downloaded' THEN 1          ELSE 0      END) AS "Contagem de Downloads",     SUM(CASE          WHEN l.eventname = '\\core\\event\\resource_viewed' THEN 1          ELSE 0      END) AS "Contagem de Visualizações" FROM      mdl_files AS f JOIN      mdl_logstore_standard_log AS l      ON l.contextinstanceid = f.id WHERE      (l.eventname = '\\core\\event\\user_downloaded'      OR l.eventname = '\\core\\event\\resource_viewed')     AND f.component = 'mod_resource'     AND f.filearea = 'content' GROUP BY      f.id, f.filename, f.filepath ORDER BY      "Contagem de Downloads" DESC, "Contagem de Visualizações" DESC LIMIT 10;
