SELECT     q.name AS "Pergunta",     a.value AS "Valor Registrado",     q.presentation AS "Definição das Opções",     COUNT(*) AS "Total" FROM      mdl_feedback fb JOIN      mdl_feedback_item q ON q.feedback = fb.id LEFT JOIN      mdl_feedback_completed fc ON fc.feedback = fb.id LEFT JOIN      mdl_feedback_value a ON a.completed = fc.id AND a.item = q.id WHERE      fb.course IS NOT NULL     AND a.value IS NOT NULL GROUP BY      q.name, a.value, q.presentation ORDER BY      q.name;
