SELECT     u.id AS "ID do Usuário",     CONCAT(u.firstname, ' ', u.lastname) AS "Nome do Aluno",     c.id AS "Id do Curso",     c.fullname AS "Nome do Curso",     b.id AS "Id da Badge",     b.name AS "Nome da Badge",     CASE         WHEN b.type = 1 THEN 'Badge do Curso'         WHEN b.type = 2 THEN 'Badge do Site'         ELSE 'Desconhecido'     END AS tipo_premio,     FROM_UNIXTIME(bu.dateissued, '%d/%m/%Y %H:%i:%s') AS "Data da Emissão" FROM     mdl_badge_issued bu JOIN     mdl_badge b ON bu.badgeid = b.id JOIN     mdl_user u ON bu.userid = u.id LEFT JOIN     mdl_course c ON b.courseid = c.id ORDER BY     c.id,     "Nome do Aluno",     "Data da Emissão";
